import React, { Component } from "react";
import NewsItem from "./NewsItem";

export default class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      page: 1
    };
  }

  async componentDidMount() {
    let url =
      "https://newsapi.org/v2/top-headlines?country=us&apiKey=ae3111fcefda4046abfa617f5c1aa0af&page=1";
    let data = await fetch(url);
    let parseData = await data.json();
    console.log(parseData);
    this.setState({
      articles: parseData.articles,
    });
  }

  


  render() {
    return (
      <div>
        <div className="container my-3">
          <div className="row">
            {this.state.articles.map((element, index) => {
             return <div className="col-md-4" key={index}>
                <NewsItem title={element.title} Description={element.description} Imageurl={element.urlToImage} Newsurl={element.url} />
              </div>;
            })}
          </div>
        </div>

            <div className="container d-flex justify-content-between">
                <button type="button" className="btn btn-dark" onClick={this.handleprevclick}>Previous</button>
                <button type="button" className="btn btn-dark" onClick={this.handleNextclick}>Next</button>
            </div>

      </div>
    );
  }
}
