import React, { Component } from "react";

export default class NewsItem extends Component {
  render() {
    const { title, Imageurl, Newsurl, Description, Author, date, source } =
      this.props;

    return (
      <div>
        <div className="card position-relative">
          <span className="position-absolute top-5 left-50 translate-middle badge rounded-pill bg-danger" id="ba" style={{left: '86%', zIndex: '1', top: '2%'}}>
            <span className="visually-hidden"></span>
              {source}
          </span>
          <img src={Imageurl} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{title}...</h5>
            <p className="card-text">{Description}...</p>
            <p className="card-text">
              <small className="text-body-secondary">
                By {Author} on {new Date(date).toGMTString()}
              </small>
            </p>
            <a href={Newsurl} target="_blank" className="btn btn-dark">
              {" "}
              Read More
            </a>
          </div>
        </div>
      </div>
    );
  }
}
